import React, { useState, useEffect } from 'react';
import { googleLogout, useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';

const GoogleLogin = () => {
    const [userToken, setUserToken] = useState(() => JSON.parse(sessionStorage.getItem('user')) || null); 
    const [profile, setProfile] = useState(() => JSON.parse(sessionStorage.getItem('profile')) || null); 
    const [isLoggedIn, setIsLoggedIn] = useState(!!sessionStorage.getItem('user')); 

    const login = useGoogleLogin({
        onSuccess: (codeResponse) => {
            setUserToken(codeResponse);
            setIsLoggedIn(true);
            sessionStorage.setItem('user', JSON.stringify(codeResponse));
        },
        onError: (error) => console.log('Login Failed:', error)
    });

    useEffect(() => {        
        if (userToken && !profile) {
            axios
                .get(`https://www.googleapis.com/oauth2/v1/userinfo?access_token=${userToken?.access_token}`, {
                    headers: {
                        Authorization: `Bearer ${user?.access_token}`,
                        Accept: 'application/json',
                    },
                })
                .then((res) => {
                    setProfile(res?.data);
                    sessionStorage.setItem('profile', JSON.stringify(res?.data)); 
                })
                .catch((err) => console.log(err));
        }
        console.log("profile", profile);

    }, [userToken, profile]);

    const handleLogout = () => {
        googleLogout();
        setProfile(null);
        setUserToken(null);
        setIsLoggedIn(false);
        sessionStorage.removeItem('user');
        sessionStorage.removeItem('profile'); 
    };

    const handleGmailLogin = () => {
        login(); 
    };

    return (
        <>
            {isLoggedIn && profile ? (
                <div className="d-flex align-items-center">
                    <span className="me-2">{profile?.name}</span>
                    <img
                        src={profile?.picture}
                        alt="User"
                        className="rounded-circle me-2"
                        style={{ width: '30px', height: '30px' }}
                    />
                    <button className="btn btn-sm btn-light" onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <button className="btn btn-sm btn-light loginBtn" onClick={handleGmailLogin}>
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 24 24"
                        fill="currentColor"
                        style={{ height: '1rem', width: '1rem' }}
                    >
                        <path d="M20.283 10.356h-8.327v3.451h4.792c-.446 2.193-2.313 3.453-4.792 3.453a5.27 5.27 0 0 1-5.279-5.28 5.27 5.27 0 0 1 5.279-5.279c1.259 0 2.397.447 3.29 1.178l2.6-2.599c-1.584-1.381-3.615-2.233-5.89-2.233a8.908 8.908 0 0 0-8.934 8.934 8.907 8.907 0 0 0 8.934 8.934c4.467 0 8.529-3.249 8.529-8.934 0-.528-.081-1.097-.202-1.625z" />
                    </svg>
                    <span className='ms-2'>Login with Gmail</span>
                </button>
            )}
        </>
    );
};

export default GoogleLogin;
